import Vue from "vue";
import Vuex from 'vuex'

import db from '@/api/db';

Vue.use(Vuex);

export default new Vuex.Store({
    state: {
      cursos: []
    },
    actions: {
        async deleteCurso(cursos) {
            await db.deleteCurso(cursos)
        },
        async getCursos(context){
            context.state.cursos = [];
            let curso = await db.getCursos();
            curso.forEach(c => {
                context.state.cursos.push(c);
            })
        },
        async savecurso(cursos){
            await db.saveCurso(cursos);
          },
    }
});
